def send_news_to_subscribers():
    latest_news = get_latest_news()
    
    for chat_id, subscribed_articles in subscribed_users.items():
        for title, link in latest_news:
            if title not in subscribed_articles:
                bot.send_message(chat_id, f"üì∞ –ù–æ–≤–∏–Ω–∞: {title}\nüîó –ü–æ—Å–∏–ª–∞–Ω–Ω—è: {link}")
                subscribed_users[chat_id].append(title)

@bot.message_handler(commands=['reply_to_client'])
def reply_to_client(message):
    # –ü–µ—Ä–µ–≤—ñ—Ä–∫–∞, —á–∏ –∫–æ—Ä–∏—Å—Ç—É–≤–∞—á –≤–≤–æ–¥–∏—Ç—å —ñ–¥–µ–Ω—Ç–∏—Ñ—ñ–∫–∞—Ç–æ—Äx –∫–ª—ñ—î–Ω—Ç–∞
    if len(message.text.split()) > 1:
        client_id = message.text.split()[1]  # –û—Ç—Ä–∏–º–∞—Ç–∏ id –∫–ª—ñ—î–Ω—Ç–∞ –∑ –∫–æ–º–∞–Ω–¥–∏
        client_id = int(client_id)  # –ü–µ—Ä–µ–≤–µ—Å—Ç–∏ id –≤ —Ü—ñ–ª–æ—á–∏—Å–µ–ª—å–Ω–µ –∑–Ω–∞—á–µ–Ω–Ω—è
        if client_id in clients:
            # –ü–µ—Ä–µ–≤—ñ—Ä–∫–∞, —á–∏ –∫–ª—ñ—î–Ω—Ç –∑ —Ç–∞–∫–∏–º id —ñ—Å–Ω—É—î —É —Å–ª–æ–≤–Ω–∏–∫—É –∫–ª—ñ—î–Ω—Ç—ñ–≤
            bot.send_message(message.chat.id, f"–í—ñ–¥–ø–æ–≤—ñ–¥—å –Ω–∞ –ø–∏—Ç–∞–Ω–Ω—è –∫–ª—ñ—î–Ω—Ç–∞ @{clients[client_id]['username']}:")
            # –û—Ç—Ä–∏–º–∞—Ç–∏ —Ç–µ–∫—Å—Ç –≤—ñ–¥–ø–æ–≤—ñ–¥—ñ –≤—ñ–¥ —Ñ–∞—Ö—ñ–≤—Ü—è
            reply_text = " ".join(message.text.split()[2:])
            # –í—ñ–¥–ø—Ä–∞–≤–∏—Ç–∏ –≤—ñ–¥–ø–æ–≤—ñ–¥—å –∫–ª—ñ—î–Ω—Ç—É
            bot.send_message(clients[client_id]['chat_id'], reply_text)
        else:
            bot.send_message(message.chat.id, "–ö–ª—ñ—î–Ω—Ç –∑ —Ç–∞–∫–∏–º ID –Ω–µ –∑–Ω–∞–π–¥–µ–Ω–∏–π.")
    else:
        bot.send_message(message.chat.id, "–ë—É–¥—å –ª–∞—Å–∫–∞, –≤–≤–µ–¥—ñ—Ç—å ID –∫–ª—ñ—î–Ω—Ç–∞ —Ç–∞ —Ç–µ–∫—Å—Ç –≤—ñ–¥–ø–æ–≤—ñ–¥—ñ —É —Ñ–æ—Ä–º–∞—Ç—ñ: /reply_to_client [ID] [—Ç–µ–∫—Å—Ç –≤—ñ–¥–ø–æ–≤—ñ–¥—ñ]")    

